<?phpclass UsersController extends AppController{function login(){if($this->request->is('post')){if($this->Auth->login()){return $this->redirect(array('controller'=>'pages','action'=>'index','admin'=>true));}else{$this->Session->setFlash("votre login ou votre mot de passe ne correspond pas ");}}}function logout(){$this->Auth->logout();$this->Session->setFlash("vous etre maintenain deconncter "); $this->redirect(array('controller'=>'pages','action'=>'show','admin'=>false));}}?>